sys_hub_input_scripts.script:
{"text_prompt":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/

var knowledge_description = fd_data.subflow_inputs.knowledge_record.short_description;
var instruction_text = "Create a draft knowledge document in HTML format for the following topic: ";
var ai_prompt = instruction_text + knowledge_description;

return ai_prompt;"}}

{"clean_response":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/

var ai_response = fd_data._3__for_each.item.text;
var trimmed_response = ai_response.replace(/^\n+/g, "");

return trimmed_response;"}}

